import React from 'react'
import MailchimpSubscribe from 'react-mailchimp-subscribe'

export default function Subscribe(props) {
  return (
    <React.Fragment>
      <style jsx>{`
        .subscribe {
          margin: 0px;
          padding: 0px;
          background: #FFFFFF;
          border: 5px solid #F1F1F1;
          box-sizing: border-box;
          border-radius: 41px;
          margin-top: 30px;
          margin-bottom: 30px;
          width: 50%;
          position: relative;
        }

        @media only screen and (max-width: 1000px) {
          .subscribe {
            width: 100%;
          }
        }

        .subscribe input[type="text"] {
          border: 0px;
          padding: 20px;
          font-size: 18px;
          color: #333;
          font-family: 'hk_groteskmedium', helvetica;
          background: transparent;
          outline: none;
          width: 100%;
        }

        .subscribe button {
          background: #F1F1F1;
          border-radius: 0px 33px 33px 0px;
          position: absolute;
          width: 150px;
          height: 100%;
          right: 0px;
          top: 0px;
          border: none;
          font-size: 18px;
          font-family: 'hk_groteskmedium', helvetica;
          color: #0D0D0D;
          transition: opacity 0.25s linear;
          opacity: 1;
          cursor: pointer;
        }

        .subscribe button:hover {
          opacity: 0.75;
        }
      `}</style>

      <MailchimpSubscribe url="https://weekday.us3.list-manage.com/subscribe/post?u=5db567ea876fe7ae1444db9f3&amp;id=851e307826"/>

      <div className="subscribe">
        <input type="text" />

        <button>Subscribe</button>
      </div>
    </React.Fragment>
  )
}
