import { useRouter } from 'next/router';
import fetch from 'isomorphic-unfetch'

function Index(props) {
  return (
    <React.Fragment>
      <h1>{props.email}</h1>
      <h1>{props.token}</h1>
      <h1>{props.error}</h1>
    </React.Fragment>
  );
}

Index.getInitialProps = async function(context) {
  try {
    const payload = Buffer.from(context.query.token, 'base64').toString('ascii');
    const { token, email } = JSON.parse(payload);

    return {
      token,
      email,
      error: null,
    }
  } catch (e) {
    return {
      token: null,
      email: null,
      error: 'Sorry there has been an error'
    }
  }

