import { useRouter } from 'next/router';
import fetch from 'isomorphic-unfetch'
import { useEffect, useState } from 'react'

function Index(props) {
  const router = useRouter();

  useEffect(() => {



  }, [])
  return (
    <React.Fragment>
      <h1>{router.query.token}</h1>
      <h1>{props.email}</h1>
      <h1>{props.token}</h1>
      <h1>{props.error}</h1>
    </React.Fragment>
  );
}

Index.getInitialProps = async function(context) {
  try {
    const payload = btoa(context.query.token);
    const { token, email } = JSON.parse(payload);
    const res = await fetch('http://localhost:8181/api/v1/auth/confirm', {
      method: 'POST',
      mode: 'cors',
      cache: 'no-cache',
      credentials: 'same-origin',
      headers: { 'Content-Type': 'application/json' },
      redirect: 'follow',
      referrer: 'no-referrer',
      body: payload,
    });
    const data = await res.json();

    if (!data.success) {
      return {
        token: null,
        email: null,
        error: 'Sorry there has been an error'
      }
    }

    return {
      token,
      email,
      error: null,
    }
  } catch (e) {
    return {
      token: null,
      email: null,
      error: 'Sorry there has been an error'
    }
  }
};

export default Index;
